import React from 'react'
import AppBar from '@material-ui/core/AppBar'
import Toolbar from '@material-ui/core/Toolbar'
import Button from '@material-ui/core/Button'
import { makeStyles, useTheme } from '@material-ui/core/styles'
import AnchorLink from 'react-anchor-link-smooth-scroll'
import IconButton from '@material-ui/core/IconButton';
import clsx from 'clsx';
import MenuIcon from '@material-ui/icons/Menu';
import Drawer from '@material-ui/core/Drawer';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';
import Divider from '@material-ui/core/Divider';

const useStyles = makeStyles((theme) => ({
    anchor: {
        textDecoration : 'none',
        color : 'inherit'
    },
    drawer: {
        width: 240,
        flexShrink: 0,
      },
}));

function Header(props) {
    const classes = useStyles();
    const [open, setOpen] = React.useState(false);
    const theme = useTheme();
    // const setTag = props.setTag;
    // console.log(setTag);

    const handleDrawerOpen = () => {
        setOpen(true);
    };

    const handleDrawerClose = () => {
        setOpen(false);
    };

    function handleTag(tags){
        props.setTag(tags);
        console.log(tags);
    }

    return (
        <>
        <AppBar
        color='default'
        position='static'
        >
            <Toolbar>
            <IconButton
            color="inherit"
            aria-label="open drawer"
            onClick={handleDrawerOpen}
            edge="start"
            className={clsx(classes.menuButton, open && classes.hide)}
          >
            <MenuIcon />
          </IconButton>
          <Drawer
            className={classes.drawer}
            variant="persistent"
            anchor="left"
            open={open}
            // classes={{
            // paper: classes.drawerPaper,
            // }}
        >
        <div className={classes.drawerHeader}>
          <IconButton onClick={handleDrawerClose}>
            {theme.direction === 'ltr' ? <ChevronLeftIcon /> : <ChevronRightIcon />}
          </IconButton>
        </div>
        <Divider />
                <Button onClick={handleTag("bag")} >bag</Button>
                <Button onClick={handleTag("cap")}>cap</Button>
                <Button onClick={handleTag("glass")}>glass</Button>
                <Button onClick={handleTag("jacket")}>jacket</Button>
                <Button onClick={handleTag("jeans")}>jeans</Button>
                <Button onClick={handleTag("shirt")}>shirt</Button>
                <Button onClick={handleTag("shoes")}>shoes</Button>
                <Button onClick={handleTag("all")} >all</Button>
      </Drawer>
      <div style={{ alignItems: 'center' }}>
                <AnchorLink href="#about"
                    className={classes.anchor}>
                    <Button>
                        HOME
                    </Button>
                </AnchorLink>

                <AnchorLink href="#contact" className={classes.anchor}>
                    <Button color='inherit'>
                        CONTACT
                    </Button>
                </AnchorLink>
        </div>
            </Toolbar>
        </AppBar>
    </>
    )
}

export default Header
