import React, { useState, useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import CardHeader from '@material-ui/core/CardHeader';
import Avatar from '@material-ui/core/Avatar';
import IconButton from '@material-ui/core/IconButton';
import StarBorderOutlinedIcon from '@material-ui/icons/StarBorderOutlined';
import { CardMedia, Container } from '@material-ui/core';
import StarIcon from '@material-ui/icons/Star';
import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';
import axios from 'axios';

const useStyles = makeStyles((theme) => ({
    modal: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
    },
    paper: {
      backgroundColor: theme.palette.background.paper,
      border: '2px solid #000',
      boxShadow: theme.shadows[5],
    //   padding: theme.spacing(2, 4, 3),
    },
    bullet: {
        display: 'inline-block',
        margin: '0 2px',
        transform: 'scale(0.8)',
      },
      title: {
        fontSize: 14,
      },
      pos: {
        marginBottom: 12,
      },
      media: {
          height: 0,
          paddingTop: '56.25%', // 16:9
          backgroundSize : 'contain',
      },
      card: {
          '@media (min-width:400px)': {
              height: '570px',
            },
      },
      margins: {
          marginBottom: '12px',
      },
  }));


function BodyCard(props) {
    const { category, description, id, image, price, title } = props;
    const classes = useStyles();
    const bull = <span className={classes.bullet}>•</span>;

    const [starState, setState] = useState(false);
    const [open, setOpen] = React.useState(false);
    const [post, setPosts] = useState([])

    const handleOpen = () => {
        setOpen(true);
        console.log(id);
        axios.get('https://ca-tech-challenge-web-202103.herokuapp.com/products/:'+id)
        .then(res => {
            setPosts(res.data.data)
        })
    };

    const handleClose = () => {
        setOpen(false);
    };

    useEffect(() => {
        axios.get('https://ca-tech-challenge-web-202103.herokuapp.com/products/:'+id)
        .then(res => {
            setPosts(res.data.data)
        })
    }, [])

    let button = <StarBorderOutlinedIcon />;

    if(starState){
        button = <StarIcon />;
    } else {
        button = <StarBorderOutlinedIcon />;
    }

    return (
        <Card className={classes.card} variant="outlined">
            <CardHeader
                action={
                <IconButton aria-label="star" onClick={() => setState(!starState)}>
                    {button}
                </IconButton>
                }
                title={title}
            />
            <CardMedia className={classes.media} image={image} />
            <CardContent>
            <Typography variant="body1" component="p" className={classes.margins}>
                {description}
            </Typography>
            <Typography variant="h5" component="p">
                ￥ {price}
            </Typography>
            </CardContent>
            <CardActions>
            <Button size="small" onClick={handleOpen}>詳細をみる</Button>
            </CardActions>
            <Modal
                aria-labelledby="transition-modal-title"
                aria-describedby="transition-modal-description"
                className={classes.modal}
                open={open}
                onClose={handleClose}
                closeAfterTransition
                BackdropComponent={Backdrop}
                BackdropProps={{
                timeout: 500,
                }}
            >
                <Fade in={open}>
                <div className={classes.paper}>
                    <h2 id="transition-modal-title">Transition modal</h2>
                    <p id="transition-modal-description">react-transition-group animates me.</p>
                </div>
                <Card className={classes.card} variant="outlined">
                    <CardHeader
                    action={
                    <IconButton aria-label="star" onClick={() => setState(!starState)}>
                        {button}
                    </IconButton>
                    }
                    title={title}
                    />
                    <CardMedia className={classes.media} image={image} />
                    <CardContent>
                    <Typography variant="body1" component="p" className={classes.margins}>
                        {description}
                    </Typography>
                    <Typography variant="h5" component="p">
                        ￥ {price}
                    </Typography>
                    </CardContent>
                </Card>
                </Fade>
            </Modal>
        </Card>
    );
}


export default BodyCard
