import React, {useState, useEffect} from 'react';
import axios from 'axios';

import { Grid } from '@material-ui/core'
import BodyCard from './BodyCard'

const cardContent = {}

function ModalButton(props) {
    console.log('props'+props.id);
    const { category, description, id, image, price, title } = props;
    const [post, setPosts] = useState([])

      useEffect(() => {
        axios.get('https://ca-tech-challenge-web-202103.herokuapp.com/products/:'+id)
        .then(res => {
            console.log(id);
            console.log(res);
            setPosts(res.data.data)

        })
    }, [])

    const getCardContent = getObj => {
        const bodyCardContent = {...getObj, ...cardContent};
        return (
            <Grid item xs={12} sm={4} key={getObj.id}>
                <BodyCard {...bodyCardContent} />
            </Grid>
        );
    };
    return (
        <Grid container spacing={2}>
            {post.map(contentObj => getCardContent(contentObj))}
        </Grid>
    )
}

export default ModalButton;
