import React from 'react'
import AppBar from '@material-ui/core/AppBar'
import Toolbar from '@material-ui/core/Toolbar'
import Button from '@material-ui/core/Button'
import { makeStyles, useTheme } from '@material-ui/core/styles'
import AnchorLink from 'react-anchor-link-smooth-scroll'
import IconButton from '@material-ui/core/IconButton';
import clsx from 'clsx';
import MenuIcon from '@material-ui/icons/Menu';
import Drawer from '@material-ui/core/Drawer';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';
import Divider from '@material-ui/core/Divider';
import Box from '@material-ui/core/Box';
import { Grid, Typography } from '@material-ui/core'
import AccountCircleIcon from '@material-ui/icons/AccountCircle';
import axios from 'axios';
import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';
import ModalButton from './ModalButton';

const useStyles = makeStyles((theme) => ({
    anchor: {
        textDecoration : 'none',
        color : 'inherit'
    },
    drawer: {
        width: 200,
        flexShrink: 0,
      },
      fonts:{
        '@media (min-width:400px)' :{
        fontSize : '50px',
      }
    },
    menuButton: {
        paddingLeft: theme.spacing(2),
      },
      hide: {
        display: 'none',
      },
      modal: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
      },
}));

function Header(props) {
    const classes = useStyles();
    const [open, setOpen] = React.useState(false);
    const theme = useTheme();
    const setTag = props.setTag;

    const [user, setUser] = React.useState({ 
        "email":"",
        "userId": "",
        "password":"",
        "name":{
            "firstname":"",
            "lastname":"",
        },
        "phone":"" });

    const handleDrawerOpen = () => {
        setOpen(true);
    };

    const handleDrawerClose = () => {
        setOpen(false);
    };

    function handleTag(tags){
        setTag(tags);
    }

    const users = {
        email:"abc",
        userId: "abc",
        password:"abc",
        name:{
            firstname:"abc",
            lastname:"abc",
        },
        phone:"abc"
      };

    function createUser(){
        axios.post(`https://ca-tech-challenge-web-202103.herokuapp.com/users`,  users )
      .then(res => {
        console.log(res);
        console.log(res.data);
      })
    }

    function getUser(){
        axios.get(`https://ca-tech-challenge-web-202103.herokuapp.com/users/`+"abc")
      .then(res => {
        console.log(res);
        console.log(res.data);
      })
    }

    const handleOpen = () => {
        setOpen(true);
        // setPathId(id);
    };

    const handleClose = () => {
        setOpen(false);
    };

    return (
        <AppBar
        color='default'
        position='static'
        >
            <Toolbar>
            <IconButton
            color="inherit"
            aria-label="open drawer"
            onClick={handleDrawerOpen}
            edge="start"
            className={clsx(classes.menuButton, open && classes.hide)}
          >
            <MenuIcon />
          </IconButton>
          <Drawer
            className={classes.drawer}
            variant="persistent"
            anchor="left"
            open={open}
            classes={{
            paper: classes.drawerPaper,
            }}
        >
        <div className={classes.drawerHeader}>
          <IconButton onClick={handleDrawerClose}>
            {theme.direction === 'ltr' ? <ChevronLeftIcon /> : <ChevronRightIcon />}
          </IconButton>
        </div>
        <Divider />
                <Button onClick={function(){handleTag("bag")}} >bag</Button>
                <Button onClick={function(){handleTag("cap")}}>cap</Button>
                <Button onClick={function(){handleTag("glass")}}>glass</Button>
                <Button onClick={function(){handleTag("jacket")}}>jacket</Button>
                <Button onClick={function(){handleTag("jeans")}}>jeans</Button>
                <Button onClick={function(){handleTag("shirt")}}>shirt</Button>
                <Button onClick={function(){handleTag("shoes")}}>shoes</Button>
                <Button onClick={function(){handleTag("all")}}>all</Button>
      </Drawer>
      {/* <IconButton onClick={createUser}> */}
            {/* <AccountCircleIcon style={{fontSize: 'inherit',}}/> */}
        {/* </IconButton> */}
        <Button onClick={handleOpen}>Sign Up</Button>
        <Modal
                aria-labelledby="transition-modal-title"
                aria-describedby="transition-modal-description"
                className={classes.modal}
                open={open}
                onClose={handleClose}
                closeAfterTransition
                BackdropComponent={Backdrop}
                BackdropProps={{
                timeout: 500,
                }}
            >
                <Fade in={open}>
                <div className={classes.paper}>
                    <Typography>会員登録</Typography>
                    <form>
                    <label>
                        Name:
                        <input type="text" name="name" />
                    </label>
                    <input type="submit" value="Submit" />
                    </form>
                </div>
                </Fade>
            </Modal>
    </Toolbar>
    </AppBar>
    )
}

export default Header
