import React, {useState, useEffect} from 'react';
import axios from 'axios';

import { Grid } from '@material-ui/core'
import BodyCard from './BodyCard'
import { makeStyles } from '@material-ui/core/styles'
import Button from '@material-ui/core/Button';
import Slider from '@material-ui/core/Slider';
import Typography from '@material-ui/core/Typography';
import Grow from '@material-ui/core/Grow';
import Paper from '@material-ui/core/Paper';
import Popper from '@material-ui/core/Popper';
import MenuItem from '@material-ui/core/MenuItem';
import MenuList from '@material-ui/core/MenuList';
import ClickAwayListener from '@material-ui/core/ClickAwayListener';

const cardContent = {}

const useStyles = makeStyles((theme) => ({
    margins : {
        marginBottom : '20px',
    },
    sliderLength : {
        width : 300,
    },
    paper: {
        marginRight: theme.spacing(2),
      },
}));

const marks = [
    {
      value: 0,
      label: '￥4000',
    },
    // {
    //   value: 5000,
    //   label: '￥5000',
    // },
    {
      value: 50,
      label: '￥1502000',
    },
    {
      value: 100,
      label: '￥3000000',
    },
  ];

function valuetext(value) {
    return `${value}`;
  }

function Content() {
    const classes = useStyles();
    const [post, setPosts] = useState([])

    const [open, setOpen] = React.useState(false);
  const anchorRef = React.useRef(null);

  const handleToggle = () => {
    setOpen((prevOpen) => !prevOpen);
  };

  const handleClose = (event) => {
    if (anchorRef.current && anchorRef.current.contains(event.target)) {
      return;
    }

    setOpen(false);
  };

  function handleListKeyDown(event) {
    if (event.key === 'Tab') {
      event.preventDefault();
      setOpen(false);
    }
  }

    function sortPrice() {
        axios.get('https://ca-tech-challenge-web-202103.herokuapp.com/products?sort=desc&sortKey=price')
        .then(res => {
            setPosts(res.data.data)
        })
    }

    function sortDefault() {
        axios.get('https://ca-tech-challenge-web-202103.herokuapp.com/products')
        .then(res => {
            setPosts(res.data.data)
        })
    }

      useEffect(() => {
        axios.get('https://ca-tech-challenge-web-202103.herokuapp.com/products')
        .then(res => {
            setPosts(res.data.data)
        })
    }, [])

    const prevOpen = React.useRef(open);
  React.useEffect(() => {
    if (prevOpen.current === true && open === false) {
      anchorRef.current.focus();
    }

    prevOpen.current = open;
  }, [open]);

    const getCardContent = getObj => {
        const bodyCardContent = {...getObj, ...cardContent};
        return (
            <Grid item xs={12} sm={4} key={getObj.id}>
                <BodyCard {...bodyCardContent} />
            </Grid>
        );
    };
    return (
        <>
            <Button variant="contained" onClick={sortDefault} className={classes.margins}>Default</Button>
            <Button variant="contained" onClick={sortPrice} className={classes.margins}>Sort by price</Button>
            <div className={classes.sliderLength}>
                <Typography id="discrete-slider-always" gutterBottom>
                    Always visible
                </Typography>
                <Slider
                    defaultValue={50}
                    getAriaValueText={valuetext}
                    aria-labelledby="discrete-slider-always"
                    step={10}
                    marks={marks}
                    valueLabelDisplay="on"
                />
            </div>
            <div>
        <Button
          ref={anchorRef}
          aria-controls={open ? 'menu-list-grow' : undefined}
          aria-haspopup="true"
          onClick={handleToggle}
        >
          Toggle Menu Grow
        </Button>
        <Popper open={open} anchorEl={anchorRef.current} role={undefined} transition disablePortal>
          {({ TransitionProps, placement }) => (
            <Grow
              {...TransitionProps}
              style={{ transformOrigin: placement === 'bottom' ? 'center top' : 'center bottom' }}
            >
              <Paper>
                <ClickAwayListener onClickAway={handleClose}>
                  <MenuList autoFocusItem={open} id="menu-list-grow" onKeyDown={handleListKeyDown}>
                    <MenuItem onClick={handleClose}>Profile</MenuItem>
                    <MenuItem onClick={handleClose}>My account</MenuItem>
                    <MenuItem onClick={handleClose}>Logout</MenuItem>
                  </MenuList>
                </ClickAwayListener>
              </Paper>
            </Grow>
          )}
        </Popper>
      </div>
            <Grid container spacing={1}>
                {post.map(contentObj => getCardContent(contentObj))}
            </Grid>
        </>
    )
}

export default Content
